import React, { useEffect, useState } from "react";

function App() {
  const[apidata,setapidata]=useState();
  const[city,setcity]=useState("mumbai");
  const addaction= async()=>{
    let url=`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=7f40a7595102633b575991d81ac826cd`
    const res = await fetch(url);
    const data = await res.json();
    setapidata(data.main);
    console.log(apidata);
  }
   useEffect(()=>{addaction();},[city]);
  return (
  <>
  <div className="container">
    <div className="row">
     <div className="col-md-5 col 5 mx-auto">
     <div className="header">
      <input type="text" onChange={(e)=>{setcity(e.target.value)}} />
     </div>
     {!setapidata?
   ( <p>no data</p> ):
   (
    <div>
         <div className="body">
           <div>{apidata.temp}</div>
           <div>wind</div>
         </div>
         <div className="footer">
          <div>{apidata.temp_min}</div>
          <div>{Math.floor(apidata.temp_max*10)}</div>
         </div>
         </div>)
}
     </div>
    </div>

  </div>
  </>
  );
}

export default App;
