import React, { useEffect, useState } from "react";

function App() {
  const[apidata,setapidata]=useState();
  //const[weather,setweather]=useState();
  const[city,setcity]=useState("pune");
  const addaction= async()=>{
    let url=`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7f40a7595102633b575991d81ac826cd`
    const res = await fetch(url);
    const data = await res.json();
    console.log(data);
    setapidata(data);

  }

   useEffect(()=>{addaction();},[city]);
  return (
  <>
  <div className="container">
    <div className="row mx-auto">
     <div className="col-md-7 col 7  col-lg-7 mx-auto mt-5 ">
      <div className="card p-3 content"style={{width:'20rem'}}>
     <div className="header mt-5">
      <input type="text" onChange={(e)=>{setcity(e.target.value)}} />
     </div>
     {!apidata ?
   (  <>
    <div className="body mt-5">
      <div>temp</div>
      <div>wind</div>
    </div>
    <div className="mt-5">feels like</div>
    <div className="footer mt-5 mb-5">
    <div>min</div>
     <div>max</div>
    </div>
    <div className="footer mt-5 mb-5">
    <div>sun rise</div>
     <div>sun set</div>
    </div>
    <div className="mt-5 mb-5">Time</div>
    </> ):
   (
    <>
    <div className="body mt-5">
      <div>{apidata.main.temp}</div>
      <div>{(apidata.weather[0].main==='cloud') ?   <span class="material-symbols-outlined">
      filter_drama
      </span> : <span class="material-symbols-outlined">sunny</span>
      }
      </div>
    </div>
    <div className="mt-5">Feels Like{apidata.main.feels_like}</div>
    <div className="footer mt-5 mb-5">
    <div> Min:{apidata.main.temp_min}</div>
     <div>Max:{apidata.main.temp_max}</div>
    </div>
    <div className="footer mt-5 mb-5">
    <div>{apidata.sys.sunrise}</div>
     <div>{apidata.sys.sunset}</div>
    </div>
    <div className="mt-5 mb-5">Local Time : {new Date().toLocaleTimeString()}</div>
    </> 

         )
}
</div>

     </div>
    </div>

  </div>
  </>
  );
}

export default App;
