import React, { useEffect, useState } from "react";

function App() {
  const[apidata,setapidata]=useState();
  const[city,setcity]=useState(" ");
  const addaction= async()=>{
    let url=`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7f40a7595102633b575991d81ac826cd`
    const res = await fetch(url);
    const data = await res.json();
    setapidata(data.main);
    console.log(apidata);
  }
   useEffect(()=>{addaction();},[city]);
  return (
  <>
  <div className="container">
    <div className="row">
     <div className="col-md-6 col 6 mx-auto mt-5 ">
      <div className="card p-3 content"style={{width:'20rem'}}>
     <div className="header mt-5">
      <input type="text" onChange={(e)=>{setcity(e.target.value)}} />
     </div>
     {! apidata?
   (  <>
    <div className="body mt-5">
      <div>temp</div>
      <div>wind</div>
    </div>
    <div className="mt-5">feels like</div>
    <div className="footer mt-5 mb-5">
    <div>min</div>
     <div>max</div>
    </div>
    <div className="footer mt-5 mb-5">
    <div>sun rise</div>
     <div>sun set</div>
    </div>
    <div className="mt-5 mb-5">Time</div>
    </> ):
   (
    <div>
         <div className="body mt-5">
           <div>{Math.floor(apidata.temp)}</div>
           <div>wind</div>
         </div>
         <div className="footer mt-5 mb-5">
         <div>{Math.floor(apidata.temp_min)}</div>
          <div>{Math.floor(apidata.temp_max)}</div>
         </div>
         <div>Time</div>
         </div>

         )
}
</div>

     </div>
    </div>

  </div>
  </>
  );
}

export default App;
