import React, { useEffect, useState } from "react";

function App() {
  const[apidata,setapidata]=useState(null);
 // const[weath,setweather]=useState();
  const[city,setcity]=useState("Mumbai");
  const addaction= async()=>{
    let url=`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7f40a7595102633b575991d81ac826cd`
    const res = await fetch(url);
    const data = await res.json();
    console.log(data);
    setapidata(data);

    }


   useEffect(()=>{addaction();},[city]);

  return (
  <>
  <div className="container center">
    <div className="row mx-auto center">
     <div className="col-md-4 col 10  col-lg-4 mx-auto mt-5 center ">
      <div className="card p-3 content"style={{width:'20rem'}}>
     <div className="header mt-5">
      <input type="text" placeholder="Mumbai"  onChange={(e)=>{setcity(e.target.value)}} />
     </div>
     {!(apidata) ?
   (  <>
    <div className="body mt-5">
      <div>temp</div>
      <div>wind</div>
    </div>
    <div className="mt-5">feels like</div>
    <div className="footer mt-5 mb-5">
    <div>min</div>
     <div>max</div>
    </div>
    <div className="footer mt-5 mb-5">
    <div>Humidity</div>
     <div>Country</div>
    </div>
    <div className="mt-5 mb-5">Time</div>
    </> ) :
   (
    <>
    <div className="body mt-5">
      {(apidata.main)?  <div>{apidata.temp} °C</div> : null }

      {/* <div>{(weath.main==='cloud') ?   <span className="material-symbols-outlined">
      filter_drama
      </span> :  <span className="material-symbols-outlined">sunny</span>
      }
      </div> */}
    </div>
    {(apidata.main)?  <div className="mt-5">Feels Like: {apidata.feels_like} °C</div> : null }

    <div className="footer mt-5 mb-5">
    {(apidata.main)?  <div><span> Min :{Math.floor(apidata.temp_min)} °C</span></div> : null } 
    {(apidata.main)?   <div><span>Max :{Math.floor(apidata.temp_max)} °C</span></div> : null }

    </div>
    <div className="footer mt-3 mb-3">
    {(apidata.main)?   <div><span>Humidity : {apidata.humidity}</span></div> : null }

     <div><span>Country : </span></div>
    </div>
    <div className="mt-5 mb-2">Local Time : {new Date().toLocaleTimeString()}</div>
    </> 

         )
}
</div>

     </div>
    </div>

  </div>
  </>
  );
}

export default App;
